# Compute mixing ratio climatology

import iemdb
from pyIEM import mesonet
import math, numpy
ASOS = iemdb.connect("asos", bypass=True)
acursor = ASOS.cursor()
"""
acursor.execute("SELECT extract(doy from valid) as d, dwpf from t2013 where station = 'DSM' and dwpf > -50 and dwpf < 90 and tmpf > -50 and tmpf < 120 and valid > '1950-01-01' ")

dtot = numpy.zeros( (366,), 'f')
dmin = numpy.zeros( (366,), 'f')
dmax = numpy.zeros( (366,), 'f')
dcnt = numpy.zeros( (366,), 'f')
dmin[:] = 100.

for row in acursor:
  dwpc = mesonet.f2c( row[1] )
  e  = 6.112 * math.exp( (17.67 * dwpc) / (dwpc + 243.5));
  mixr =  0.62197 * e / (1000.0 - e)
  if mixr > dmax[row[0] -1]:
    dmax[row[0] -1] = mixr
  if mixr < dmin[row[0] -1]:
    dmin[row[0] -1] = mixr

  dtot[ row[0] -1] += mixr
  dcnt[ row[0] -1] += 1

print tuple( dtot / dcnt )
"""
